package PracticePrograms;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<2;i++)
		{
			System.out.print("          ");//10 spaces
			for(int j=1;j<=1;j++)			//loops 1 times
			{
			System.out.print("*");
			}
			System.out.println();
			System.out.print("         ");//9spaces
			for(int j=1;j<=2;j++)			//loops 2 times
			{
			System.out.print("*");
			}
			System.out.println();
			System.out.print("        ");//8 spaces
			for(int j=1;j<=3;j++)			//loops 3 times
			{
			System.out.print("*");
			}
			System.out.println();
			System.out.print("       ");//7 spaces
			for(int j=1;j<=4;j++)			//loops 4 times
			{
			System.out.print("*");
			}
			System.out.println();
			System.out.print("      ");//6 spaces
			for(int j=1;j<=5;j++)			//loops 5 times
			{
			System.out.print("*");
			}
			System.out.println();
	}
	}
}
